class ApplicationChat():
    def __init__(self):
        self.CAMERA_CLOSE = False
        self.CAMERA_SAVE = False
        self.CAMERA_SEND = False

        self.Mongo = MongoDatabase()
    def Camera(self):
        CAMERA = cv2.VideoCapture(0)
        img = np.zeros((512, 512, 3), np.uint8)

        def Generate():
            String_Array = ["a", "b", "c", "d", "e", "f", "g", "h"]
            Number_Array = [1, 2, 3, 4, 5, 6, 7, 8, 9, 0]
            Random = ""
            for i in range(10):
                Random += str(random.choice(String_Array))
                Random += str(random.choice(Number_Array))
            return Random
        while True:
            ret, goruntu = CAMERA.read()
            cv2.imshow("Kamera", goruntu)
            if cv2.waitKey(25) & 0xFF == ord("q"):
                break

            elif self.CAMERA_CLOSE == True:
                break

            elif self.CAMERA_SAVE == True:
                cv2.imwrite(Generate() + ".png",goruntu)
                break

            elif self.CAMERA_SEND == True:
                Generates = Generate()


                cv2.imwrite(Generates + ".png",goruntu)
                im = Image.open(Generates + ".png")
                image_bytes = io.BytesIO()
                im.save(image_bytes, format="JPEG")
                image = {'data':image_bytes.getvalue()}
                self.Mongo.ChatDB_PHOTO.insert_one(image).inserted_id
                break

        CAMERA.release()
        cv2.destroyAllWindows()
    def Sound(self):
        THRESHOLD = 500
        CHUNK_SIZE = 1024
        FORMAT = pyaudio.paInt16
        RATE = 44100

        def is_silent(snd_data):
            return max(snd_data) < THRESHOLD
        def normalize(snd_data):
            MAXIMUM = 16384
            times = float(MAXIMUM) / max(abs(i) for i in snd_data)

            r = array('h')
            for i in snd_data:
                r.append(int(i * times))
            return r
        def trim(snd_data):
            def _trim(snd_data):
                snd_started = False
                r = array('h')

                for i in snd_data:
                    if not snd_started and abs(i) > THRESHOLD:
                        snd_started = True
                        r.append(i)

                    elif snd_started:
                        r.append(i)
                return r


            snd_data = _trim(snd_data)

            snd_data.reverse()
            snd_data = _trim(snd_data)
            snd_data.reverse()
            return snd_data
        def add_silence(snd_data, seconds):
            silence = [0] * int(seconds * RATE)
            r = array('h', silence)
            r.extend(snd_data)
            r.extend(silence)
            return r
        def record():
            p = pyaudio.PyAudio()
            stream = p.open(format=FORMAT, channels=1, rate=RATE,input=True, output=True,frames_per_buffer=CHUNK_SIZE)

            num_silent = 0
            snd_started = False

            r = array('h')

            while 1:
                snd_data = array('h', stream.read(CHUNK_SIZE))
                if byteorder == 'big':
                    snd_data.byteswap()
                r.extend(snd_data)

                silent = is_silent(snd_data)

                if silent and snd_started:
                    num_silent += 1
                elif not silent and not snd_started:
                    snd_started = True

                if snd_started and num_silent > 30:
                    break

            sample_width = p.get_sample_size(FORMAT)
            stream.stop_stream()
            stream.close()
            p.terminate()

            r = normalize(r)
            r = trim(r)
            r = add_silence(r, 0.5)
            return sample_width, r
        def record_to_file(path):
            "Records from the microphone and outputs the resulting data to 'path'"
            sample_width, data = record()
            data = pack('<' + ('h' * len(data)), *data)

            wf = wave.open(path, 'wb')
            wf.setnchannels(1)
            wf.setsampwidth(sample_width)
            wf.setframerate(RATE)
            wf.writeframes(data)
            wf.close()

        def Send_DATABASE():
            DATA = base64.b64encode(open("demo.wav","rb").read()) # Çekilen Ses kaydı Encoding Edilip DB aktarılması Gerekiyor.
            self.Mongo.Mongo_Insert_VOLUME(InsertData=DATA)

        if __name__ == '__main__':
            record_to_file('demo.wav')
            Send_DATABASE()
