    def NotebookApplication_SAYFA_SAYISI_BUTTON(self):
        def SayfaSayısı(DATA):
            NUMBER = ""
            NUMBER_LIST = ["1", "2", "3", "4", "5", "6", "7", "8", "9", "0"]
            for i in DATA.strip():
                if i in NUMBER_LIST:
                    NUMBER += i
